import Header from './components/Header'
import Tasks from './components/Tasks'
import { useState } from 'react'
import AddTask from './components/AddTask'

var feed = [];
var objFeed = [];

var wSock = new WebSocket("ws://10.0.0.5:8000/ws/test/");
  wSock.onopen = function(event){
    wSock.send("test");
  };

  wSock.onmessage = function(event){
    //console.log(event.data);
    feed = event.data.split("****");
    for(var i = 0; i < feed.length-1; i++){
      feed[i] = feed[i].split("**")
      objFeed.push({id:i,
        text:feed[i][0],
        day:feed[i][1],
        reminder:feed[i][2]}
      );
    }
    App.setState();
    console.log(feed);
  }
  console.log("test");




function App() {
  const name = 'Chad'
  const [showAddTask, setShowAddTask] = useState(false)
  const [tasks, setTasks] = useState(/*[
    // {
    //     id: 1,
    //     text: 'Doctors Appointment',
    //     day: 'Feb 5th at 2:30pm',
    //     reminder: true
    // },
    // {
    //     id: 2,
    //     text: 'Meeting at School',
    //     day: 'Feb 6th at 1:30pm',
    //     reminder: true
    // },
    // {
    //     id: 3,
    //     text: 'Grocery Shopping',
    //     day: 'Feb 5th at 2:30pm',
    //     reminder: false
    // }
  ]*/
  objFeed)
  // Add Task
  const addTask = (task) => {
    const id = Math.floor(Math.random() * 10000) + 1
    const newTask = { id, ...task }
    setTasks([...tasks, newTask])
  }

  function addLocalTask(text, day, reminder) {
    const id = Math.floor(Math.random() * 10000) + 1
    const newTask = { id, ...{text, day, reminder} }
    setTasks([...tasks, newTask])
  }

  // Delete Task
  const deleteTask = (id) => {
    //console.log('delete', id)
    setTasks(tasks.filter((task) => task.id !==id))
  }

  const clearTask = () => {
    setTasks([]);
  }

  // Toggle Reminder
  const toggleReminder = (id) => {
    setTasks(tasks.map((task) => 
    task.id === id ? { ...task, reminder: !task.reminder } : task))
  }


  return (
    <div className="container">
      <Header title={name} onAdd={() => setShowAddTask(!showAddTask)} showAddTask={showAddTask}/>
      {showAddTask && <AddTask onAdd={addTask}/>}
      {tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/>
      : 'No Tasks to Show'}
    </div>
  );
}

export default App;
