import { useState, useEffect } from 'react'
import PropTypes from 'prop-types'
import Button from './Button'


const DisplayPost = ({ data, back, select }) => {
    // const [selected, setSelected] = useState(false)
    // const [elements, setElements] = useState([])
    const [loaded, setLoaded] = useState(false)
    const [prevData, setPrevData] = useState({})
    //const body = React.createElement('p', {}, 'Lorem ipsum dolor sit amet');
    //idk if this is the best way to do this :/
    const loadPost = () => {
        // if(elements.length === 0){
            console.log("adding");
            // var newElements = []
            var mainDiv = document.getElementsByClassName("post-content")[0];
            //console.log(mainDiv);
            var tempData = data;

            var test = document.createElement("p");
            test.innerText = "test";    
            //const body = React.createElement('p', {}, 'Lorem ipsum dolor sit amet');
            mainDiv.appendChild(test);
            // newElements.push(test)
            // setElements(newElements)
            // setSelected(true)
            // look into react dom rendering and react elements
            setLoaded(true)
            setPrevData(data)
        // }

    }

    useEffect(() => {
        console.log("updated2");
        if(select === true && !loaded){
            console.log(loaded)
            loadPost()
        }
        else if(prevData != data){
            console.log('data changed')
        }
      });

    return (
        <div>
            <div className="post-content">
                <Button color='green' text={"back"} onClick={() => back(false)}/>
            </div>
        </div>
    )
}

DisplayPost.propTypes = {
    data: PropTypes.string,
    profile: PropTypes.string,
}

export default DisplayPost
