import Header from './components/Header'
import Tasks from './components/Tasks'
import TaskbarButton from './components/TaskbarButton'
import SettingsButton from './components/SettingsButton'
import { useState, useEffect } from 'react'
import AddTask from './components/AddTask'
import Login from './components/Login'
import MyProfile from './components/MyProfile'

var feed = [];


function App() {
  const [showAddTask, setShowAddTask] = useState(false)
  const [tasks, setTasks] = useState([/*{id:1, text:"matt",
    day:"test",
reminder:false, data:"https://10.0.0.5:8001/media/playlist2.m3u8"}*/])
  const [page, setPage] = useState(-1)
  //const [signedIn, setSignedIn] = useState(false)

  // oh god this is disgusting
  /*useEffect(() =>  {
    const fetchTasks = async () => {
      if(wSock){
        wSock.onopen = async function(event){
          wSock.send("test");
        };
        wSock.onmessage = async function(event){
          feed = await event.data.split("****");
          var newTasks = []
          for(var i = 0; i < feed.length-1; i++){
            feed[i] = await feed[i].split("**")
            await newTasks.push({id:i, text:feed[i][0],
              day:feed[i][1],
              reminder:false, data:feed[i][2]});
          }
          await setTasks(newTasks);
          wSock.close();
          wSock = false;
        }
      }
    }
    fetchTasks();
  })*/

  // useEffect(() =>  {
  //   async function fetchContent(){
  //     const Data={
  //       user: "matt",
  //       pass: "12345"
  //     }
  //     const otherParam={
  //       mode: 'cors',
  //       credentials: 'same-origin',
  //       //headers:{
  //       //  "content-type":"application/json; charset=UTF-8"
  //       //},
  //       body: JSON.stringify(Data),
  //       method: "POST"
  //     };
  //     const response = await fetch("https://10.0.0.5:8000/login/",otherParam)
  //     const content = await response.json();
  //     console.log(content)
  //   }
  //   fetchContent();
  // })

  // Add Task
  const addTask = (task) => {
    const id = Math.floor(Math.random() * 10000) + 1
    const newTask = { id, ...task }
    setTasks([...tasks, newTask])
  }

  // Delete Task
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !==id))
  }

  const clearTask = () => {
    setTasks([]);
  }

  // Toggle Reminder
  const toggleReminder = (id) => {
    setTasks(tasks.map((task) => 
    task.id === id ? { ...task, reminder: !task.reminder } : task))
  }

  const changePage = (pageId) => {
    setPage(pageId);
  }

  const onSignIn = async (credentials) => {
    const Data={
      user: credentials[0],
      pass: credentials[1]
    }
    const otherParam={
      mode: 'cors',
      credentials: 'same-origin',
      //headers:{
      //  "content-type":"application/json; charset=UTF-8"
      //},
      body: JSON.stringify(Data),
      method: "POST"
    };
    const response = await fetch("https://10.0.0.5:8000/login/",otherParam)
    const content = await response.json();
    if(content.valid == "YES"){
      console.log("welcome!");
      await setCreds([credentials[0], credentials[1]]);
      changePage(0);
      return true;
    }
    else{
            console.log('invalid credentials')
            await setCreds(['','']);
            return false;
    }
  }

  //need to put in checks on the backend for invalid inputs, someone could fake a request from outside our website
  //which would mean they could put whatever they wanted into inputs.
  const onSignUp = async (credentials) => {
    const Data={
      user: credentials[0],
      pass: credentials[1],
      first: credentials[2],
      last: credentials[3],
      phone: credentials[4]
    }
    const otherParam={
      mode: 'cors',
      credentials: 'same-origin',
      //headers:{
      //  "content-type":"application/json; charset=UTF-8"
      //},
      body: JSON.stringify(Data),
      method: "POST"
    };
    const response = await fetch("https://10.0.0.5:8000/signup/",otherParam)
    const content = await response.json();
    if(content.valid == "YES"){
      console.log("welcome!");
      //await setCreds([credentials[0], credentials[1]]);
      //changePage(0);
      return true;
    }
    else{
      console.log('invalid credentials')
      //await setCreds(['','']);
      return false;
    }
  }

  // login
  // idea: have some kind of verifaction system for every call to the api. Obviously don't include the password
  // every time lol. Give them some kind of key.
  const [creds, setCreds] = useState(["",""]);

  return (
    <div className="container">
      {page < 3 && <div className="main-content">
      {page === -1 && <div className="login">
        <Login onSignIn={onSignIn} onSignUp={onSignUp}/>
      </div>}
      {page === 0 && <div className="content">
        <Header title={creds[0]} onAdd={() => setShowAddTask(!showAddTask)} showAddTask={showAddTask}/>
        {showAddTask && <AddTask onAdd={addTask}/>}
        {(tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/>
        : 'No Tasks to Show')}
      </div>}
      {page === 1 && <div className="explore">
        {/* make a search component :P, just for looks atm. */}
        <div className='form-control'>
          <input type='text' placeholder='Search'/>
        </div>
        <input className='btn btn-block' type='submit' value='Search' />
        {(tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/>
        : 'No Tasks to Show')}
      </div>}
      {page === 2 && <div className="groups">

      </div>}
      </div>}
      {page === 3 && <div className="user">
        <div className="settings-bar">
          <SettingsButton text={"Profile"} onClick={() => {}}/>
          <SettingsButton text={"Following"} onClick={() => {}}/>
          <SettingsButton text={"Saved"} onClick={() => {}}/>
          <SettingsButton text={"Premium"} onClick={() => {}}/>
          <SettingsButton text={"Settings"} onClick={() => {}}/>
        </div>
        <div>
          <MyProfile />
        </div>
      </div>} 
      <div className="taskbar">
        {page > -1 && <TaskbarButton text={"Home"} id={0} onClick={changePage}/>}
        {page > -1 && <TaskbarButton text={"Explore"} id={1} onClick={changePage}/>}
        {page > -1 && <TaskbarButton text={"Groups"} id={2} onClick={changePage}/>}
        {page > -1 && <TaskbarButton text={"Me"} id={3} onClick={changePage}/>}
      </div>
    </div>
  );
}

export default App;
