import { FaTimes, FaTrashAlt } from 'react-icons/fa'
import StreamVid from './StreamVid'
import Image from './Image'
import React from 'react';
import ReactDOM from 'react-dom';
import { useState } from 'react'


const Task = ({ task, onDelete, onToggle }) => {
    const [selected, setSelected] = useState(false)

    //const body = React.createElement('p', {}, 'Lorem ipsum dolor sit amet');
    var elements = []
    const loadPost = () => {
        console.log("adding");
        var mainDiv = document.getElementsByClassName("task")[task.id];
        //console.log(mainDiv);
        var test = document.createElement("p");
        test.innerText = "test";    
        //mainDiv.appendChild(test);
        const body = React.createElement('p', {}, 'Lorem ipsum dolor sit amet');
        mainDiv.appendChild(test);
        //ReactDOM.render(body, document.getElementById('app'));
        //mainDiv.appendChild(body);
        //elements.push(React.createElement("div",null));

        //elements.push(body);
        //ReactDOM.render(body, document.getElementById("task"));
        setSelected(true)

    }

    return (
        <div className={`task ${task.reminder ? 'reminder' : ''}`} onClick={loadPost} onDoubleClick={() => onToggle(task.id)}>
            {!selected && <div>
            {/* <h3>{task.title} <FaTimes style={{color: 'red', cursor: 'pointer'}} onClick={() => onDelete(task.id)}/></h3> */}
            <h3>{task.title} <FaTimes style={{color: 'red', cursor: 'pointer'}} /></h3>
            <p>{task.owner}</p>
            <Image source={'https://10.0.0.5:8001/media/images/' + task.thumbnail}/>
            {/* <img scr="https://post.medicalnewstoday.com/wp-content/uploads/2020/02/man-exercising-plank-push-up-1200x628-facebook.jpg"></img> */}
            {/* <StreamVid url={task.data}></StreamVid> */}
            <p>views: {task.views}</p>
            {/* {React.createElement("div", {className: "contexCon"}, elements)} */}
            </div>}
            {selected && <div className={'contentSelected'}>
            </div>}
        </div>
    )
}

export default Task
