var wSock = new WebSocket("ws://10.0.0.5:8000/ws/test/");


function App() {
  const name = 'Chad'
  const [showAddTask, setShowAddTask] = useState(false)
  const [tasks, setTasks] = useState([])
  const [page, setPage] = useState(0)
  // oh god this is disgusting
  useEffect(() =>  {
    const fetchTasks = async () => {
      //const res = await fetch
      if(wSock){
      wSock.onopen = async function(event){
        wSock.send("test");
      };
      wSock.onmessage = async function(event){
         feed = await event.data.split("****");
         var newTasks = []
        for(var i = 0; i < feed.length-1; i++){
          feed[i] = await feed[i].split("**")
          await newTasks.push({id:i, text:feed[i][0],
            day:feed[i][1],
            reminder:false, data:feed[i][2]});
      }
      await setTasks(newTasks);
    wSock.close();
    wSock = false;
      }
    }
    }
    fetchTasks();
  })


  // Add Task
  const addTask = (task) => {
    const id = Math.floor(Math.random() * 10000) + 1
    const newTask = { id, ...task }
    setTasks([...tasks, newTask])
  }

  // function addLocalTask(text, day, reminder) {
  //   const id = Math.floor(Math.random() * 10000) + 1
  //   const newTask = { id, ...{text, day, reminder} }
  //   setTasks([...tasks, newTask])
  // }

  // Delete Task
  const deleteTask = (id) => {
    //console.log('delete', id)
    setTasks(tasks.filter((task) => task.id !==id))
  }

  const clearTask = () => {
    setTasks([]);
  }

  // Toggle Reminder
  const toggleReminder = (id) => {
    setTasks(tasks.map((task) => 
    task.id === id ? { ...task, reminder: !task.reminder } : task))
  }

  const changePage = (pageId) => {
    setPage(pageId);
    //console.log(page);
  }
  //taskbar variable (what page we are on)

  return (
    <div className="container">
      <div className="content">
      {page === 0 && <Header title={name} onAdd={() => setShowAddTask(!showAddTask)} showAddTask={showAddTask}/>}
      {page === 0 && showAddTask && <AddTask onAdd={addTask}/>}
      {page === 0 && (tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/>
      : 'No Tasks to Show')}
      </div> 
      <div className="taskbar">
        <TaskbarButton text={"Home"} id={0} onClick={changePage}/>
        <TaskbarButton text={"Explore"} id={1} onClick={changePage}/>
        <TaskbarButton text={"Groups"} id={2} onClick={changePage}/>
        <TaskbarButton text={"Me"} id={3} onClick={changePage}/>
      </div>
    </div>
  );
}

export default App;
