import { FaTimes, FaTrashAlt } from 'react-icons/fa'
import Hls from 'hls.js';

import ReactHlsPlayer from 'react-hls-player';

const StreamVid = ({ url, onStream }) => {
    const embedVideo = (url) => {
        var video = document.getElementsByClassName('vid');
        var hls = new Hls();
        hls.loadSource(url);
        hls.attachMedia(video);
      }
    embedVideo(url);
    var config = {testBandwidth: false}
      
    return (
        <div >
            {/* <video className={'vid'}></video> */}
            <ReactHlsPlayer src="https://1256304549.vod2.myqcloud.com/814a73e9vodcq1256304549/89509bab5285890787520845021/playlist.m3u8" autoPlay={false} controls={true} width="100%" height="auto" hlsConfig={config}></ReactHlsPlayer>
        </div>
    )
}

export default StreamVid
